'use strict';

module.exports = function scanRow(imageData, offset, length) {
	let highestValue = 0;

	for (let column = 0; column < length; column += 1) {
		let pixelValue = imageData[offset + (column * 4)];

		if (pixelValue > highestValue) {
			highestValue = pixelValue;
		}
	}

	return highestValue;
};
